<?php //login.phpif (isset($login) && isset($userPass)){	$felderIn_profil = "id_profil, login, pass";	$whereBedingungen = "login='$login' AND pass='$userPass'";	$result=mysql_query("SELECT $felderIn_profil FROM profil WHERE $whereBedingungen");	$row=mysql_fetch_array($result);	if(isset($row["id_profil"])){		echo "<p>&success=true&</p>\n";		echo "<p>&thisPage=$currentPage&</p>\n";		//echo "<p>"; echo htmlentities("&currentUser="); echo $row["id_profil"]; echo "&</p>\n";				session_register ("currentUser");		session_register ("currentName");		session_register ("php_desired_action");		session_register ("php_desired_id");		$currentUser=$row["id_profil"];		$currentName=$login;		$php_desired_action=$desiredAction;		$php_desired_id=$desiredId;		mysql_free_result($result);		if ($desiredAction == "/:retrievePDF"){			$result = mysql_query("SELECT id, pdf FROM vortrag WHERE id=$desiredId");			$row = mysql_fetch_array($result);			session_register ("pdf");			$pdf = $row["pdf"];			mysql_free_result($result);		}		echo "<p>&eof=true&</p>";	} else {		echo "<p>&success=false&</p>\n";		echo "<p>&eof=true&</p>";		session_unregister("currentUser");		session_unregister("currentName");	}} else {	echo "<p>&success=false&</p>\n";	echo "<p>&eof=true&</p>";	session_unregister("currentUser");	session_unregister("currentName");}?>
