<?php	$result = mysql_query("SELECT id, Titel, Zusammenfassung, gehalten, pdf, thema_id, id_thema, thema FROM vortrag AS A, thema AS B WHERE B.id_thema=A.thema_id ORDER BY gehalten DESC");	$row = mysql_fetch_array($result);	echo "<p>&hotTopic_id=".$row["id"]."&</p>\n";	echo "<p>&hotTopic_Titel=".stripslashes($row["Titel"])."&</p>\n";	echo "<p>&hotTopic_text=".stripslashes($row["Zusammenfassung"])."&</p>\n";	echo "<p>&eof=true"; echo"&</p>\n";  	mysql_free_result($result);?>
