<?phpif (empty($table)) $table="vortrag";if (empty($bereich)) $bereich="Familie";	$result = mysql_query("SELECT YEAR(gehalten) AS jahr, Familie, Arbeit, Gesundheit, Erziehung FROM $table WHERE $bereich=1 GROUP BY jahr ORDER BY jahr DESC");	$i=0;	while ($row=mysql_fetch_array($result)){		$jahr = $row["jahr"];		echo "<p>&menu_year$i=$jahr&</p>\n";		$i++;	}	echo "<p>&menu_anzahl=$i&</p>\n";	echo "<p>&eof=true"; echo"&</p>\n";  ?>
